import 'package:freezed_annotation/freezed_annotation.dart';
import 'package:hackfest_flutter/features/home/model/event.dart';

part 'model.freezed.dart';
part 'model.g.dart';

// ImageModel
@freezed
class ImageModel with _$ImageModel {
  const factory ImageModel({
    required int id,
    required ImageModelAttributes attributes,
  }) = _ImageModel;

  factory ImageModel.fromJson(Map<String, dynamic> json) => json["data"] == null ? _$ImageModelFromJson(json) : _$ImageModelFromJson(json["data"]);
}

@freezed
class ImageModelAttributes with _$ImageModelAttributes {
  const factory ImageModelAttributes({
    required String url,
    required String createdAt,
    required String updatedAt,
  }) = _ImageModelAttributes;

  factory ImageModelAttributes.fromJson(Map<String, dynamic> json) => _$ImageModelAttributesFromJson(json);
}

@freezed
class HomeModel with _$HomeModel {
  const factory HomeModel({
    required int id,
    required HomeModelAttributes attributes,
  }) = _HomeModel;

  factory HomeModel.fromJson(Map<String, dynamic> json) => _$HomeModelFromJson(json);
}

@freezed
class HomeModelAttributes with _$HomeModelAttributes {
  const factory HomeModelAttributes({
    required String title,
    required String description,
    required String createdAt,
    required String updatedAt,
    required String publishedAt,
    required String? actionUrl,
    required String? videoUrl,
    required int students,
    required int courses,
    @ListDataSerializeConverter() required List<HomeModelMentors> mentors,
    @ListDataSerializeConverter() required List<HomeModelSliders> sliders,
    @ListDataSerializeConverter() required List<HomeModelSections> sections,
    @ListDataSerializeConverter() required List<HomeModelOpinions> opinions,
  }) = _HomeModelAttributes;

  factory HomeModelAttributes.fromJson(Map<String, dynamic> json) => _$HomeModelAttributesFromJson(json);
}

@freezed
class HomeModelMentors with _$HomeModelMentors {
  const factory HomeModelMentors({
    required int id,
    required String name,
    required String field,
    required String bio,
    @PhotoModelDataConverter() required ImageModel photo,
  }) = _HomeModelMentors;

  factory HomeModelMentors.fromJson(Map<String, dynamic> json) => _$HomeModelMentorsFromJson(json);
}

@freezed
class HomeModelSections with _$HomeModelSections {
  const factory HomeModelSections({
    required int id,
    required String title,
    required String description,
    @PhotoModelDataConverter() required ImageModel photo,
  }) = _HomeModelSections;

  factory HomeModelSections.fromJson(Map<String, dynamic> json) => _$HomeModelSectionsFromJson(json);
}

@freezed
class HomeModelOpinions with _$HomeModelOpinions {
  const factory HomeModelOpinions({
    required int id,
    required String name,
    required String comment,
  }) = _HomeModelOpinions;

  factory HomeModelOpinions.fromJson(Map<String, dynamic> json) => _$HomeModelOpinionsFromJson(json);
}

@freezed
class HomeModelSliders with _$HomeModelSliders {
  const factory HomeModelSliders({
    required int id,
    required String title,
    required String description,
    @PhotoModelDataConverter() required ImageModel photo,
  }) = _HomeModelSliders;

  factory HomeModelSliders.fromJson(Map<String, dynamic> json) => _$HomeModelSlidersFromJson(json);
}
