part 'post.freezed.dart';
part 'post.g.dart';

@freezed
class Post with _$Post {
  factory Post({
    required int id,
    required PostAttributes attributes,
  }) = _Post;

  factory Post.fromJson(Map<String, dynamic> json) => _$PostFromJson(json);
}

@freezed
class PostAttributes with _$PostAttributes {
  factory PostAttributes({
    required String title,
    required String body,
    // createdAt
    required String createdAt,
    // updatedAt
    required String updatedAt,
    // description
    required String description,
    // photo
    @PhotoModelDataConverter() required ImageModel photo,
  }) = _PostAttributes;

  factory PostAttributes.fromJson(Map<String, dynamic> json) => _$PostAttributesFromJson(json);
}

